#! python
import os
import sys
import time
import base64
import random

bad = "\033[41m"
destructive = ("-D" in sys.argv or "--destructive" in sys.argv) and (
    input(f"""
{bad}Are you sure you want to run this destructive script?\033[0m
PATH: {os.getcwd()}
Files \033[1mWILL\033[0m be modified and/or deleted. This action is \033[1mirreversible\033[0m.
[\033[31my\033[0m/\033[32mN\033[0m]: """).lower() == "y")

sound = ""
# Decode the base64 and save to temp.wav
decoded = base64.b64decode(sound)
with open(".temp.wav", "wb") as f:
    f.write(decoded)
# Play the sound
try:
    os.system("cvlc .temp.wav vlc://quit --play-and-exit &> /dev/null")
finally:
    os.remove(".temp.wav")

colours = {
    "black": "\033[30m",
    "red": "\033[31m",
    "green": "\033[32m",
    "yellow": "\033[33m",
    "clear": "\033[0m",
}

print(f"Scanning drive...\n")
time.sleep(0.5)

# Read the "3.5in Floppy Disk" (or first arg if given) folder and check if its contents can fit in a 1.44MB floppy disk
folder = sys.argv[1] if len(sys.argv) > 1 else "."
ignore_this = False
# If the folder doesn't exist, use the current directory
if not os.path.exists(folder):
    folder = os.getcwd()
    ignore_this = True

size = 0
disallowed = ["CON", "PRN", "AUX", "NUL", "COM1", "COM2", "COM3", "COM4", "COM5", "COM6", "COM7", "COM8", "COM9", "LPT1", "LPT2", "LPT3", "LPT4", "LPT5", "LPT6", "LPT7", "LPT8", "LPT9"]

# Get the terminal size
rows, columns = os.popen('stty size', 'r').read().split()
rows, columns = int(rows), int(columns) - 1
block_drawing = ["█", "░"]
if destructive:
    to_check = []
    for root, dirs, files in os.walk(folder):
        for file in files:
            if ignore_this and file == "scan.py" and root == folder:
                continue
            to_check.append((root, file))
    random.shuffle(to_check)
    for root, file in to_check:
        new_name = file
        size += os.path.getsize(os.path.join(root, file))

        # If the filename has more than 8 characters (excluding the extension), trim it
        if len(os.path.splitext(file)[0]) > 8:
            new_name = os.path.splitext(file)[0][:8] + os.path.splitext(file)[1]
            print(f'\n{colours["yellow"]}Warning: The filename "{file}" has been trimmed. ({os.path.join(root, file)}){colours["clear"]}')
        # If the extension has more than 3 characters, trim it
        if len(os.path.splitext(file)[1]) > 4:
            new_name = os.path.splitext(file)[0] + os.path.splitext(file)[1][:4]
            print(f'\n{colours["yellow"]}Warning: The extension "{os.path.splitext(file)[1]}" has been trimmed. ({os.path.join(root, file)}){colours["clear"]}')
        # If it ends with a space or a period, remove it
        if file[-1] == " " or file[-1] == ".":
            new_name = file[:-1]
            print(f'\n{colours["yellow"]}Warning: The filename "{file}" has been trimmed. ({os.path.join(root, file)}){colours["clear"]}')
        # If it contains an invalid character, remove it
        if set(r"\/:*?\"<>|") & set(file):
            new_name = "".join([c for c in file if c not in r"\/:*?\"<>|"])
            print(f'\n{colours["yellow"]}Warning: The filename "{file}" has been trimmed. ({os.path.join(root, file)}){colours["clear"]}')
        # If it contains "..", remove it
        if ".." in file:
            new_name = file.replace("..", "")
            print(f'\n{colours["yellow"]}Warning: The filename "{file}" has been trimmed. ({os.path.join(root, file)}){colours["clear"]}')
        # Replace the file with the new name
        os.rename(os.path.join(root, file), os.path.join(root, new_name.lower()))
        # If the new name is disallowed, delete it entirely
        file_size = os.path.getsize(os.path.join(root, new_name.lower()))
        if os.path.splitext(new_name)[0].upper() in disallowed:
            print(f'\n{colours["red"]}Deleting: The filename "{new_name}" is disallowed. ({os.path.join(root, new_name)}){colours["clear"]}')
            for _ in range(5):
                # First overwrite the file with /dev/random
                with open(os.path.join(root, new_name.lower()), "wb") as f:
                    os.system(f"dd if=/dev/random of='{os.path.join(root, new_name.lower())}' bs={file_size} count=1 &> /dev/null")
                    time.sleep(0.1)
            # Then delete it
            os.remove(os.path.join(root, new_name.lower()))
        # If the size exceeds 1.44MB, corrupt the file and all subsequent files
        if size > 1440 * 1024:
            print(f'\n{colours["red"]}Warning: The folder size is larger than 1.44MB.\033[0m')
            for _ in range(5):
                # First overwrite the file with /dev/random
                with open(os.path.join(root, new_name.lower()), "wb") as f:
                    os.system(f"dd if=/dev/random of='{os.path.join(root, new_name.lower())}' bs={file_size} count=1 &> /dev/null")
                    time.sleep(0.1)
            os.remove(os.path.join(root, new_name.lower()))
            print(f"{colours['red']}File {new_name} deleted (corrupted).\033[0m")
        time.sleep(0.1)

for root, dirs, files in os.walk(folder):
    files_this_dir = []
    for file in files:
        if ignore_this and file == "scan.py" and root == folder:
            continue
        # Error if a filename has more than 8 characters (excluding the extension)
        if len(os.path.splitext(file)[0]) > 8:
            # Print the full path
            print(f'\n{colours["red"]}Error: The filename "{file}" has more than 8 characters. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)
        # Also error if the extension has more than 3 characters
        if len(os.path.splitext(file)[1]) > 4:
            print(f'\n{colours["red"]}Error: The extension "{os.path.splitext(file)[1]}" has more than 3 characters. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)
        if file.lower() in files_this_dir:
            print(f'\n{colours["red"]}Error: The filename "{file}" is not unique. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)
        files_this_dir.append(file.lower())

        # Do not allow a full path to be longer than 256 characters
        if len(os.path.join(root, file)) > 256:
            print(f'\n{colours["red"]}Error: The full path "{os.path.join(root, file)}" is longer than 256 characters.{colours["clear"]}')
            sys.exit(1)

        if set(r"\/:*?\"<>|") & set(file):
            print(f'\n{colours["red"]}Error: The filename "{file}" contains an invalid character. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)

        if ".." in file:
            print(f'\n{colours["red"]}Error: The filename "{file}" contains "..". ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)

        if os.path.splitext(file)[0].upper() in disallowed:
            print(f'\n{colours["red"]}Error: The filename "{file}" is disallowed. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)

        if file[-1] == " " or file[-1] == ".":
            print(f'\n{colours["red"]}Error: The filename "{file}" ends with a space or a period. ({os.path.join(root, file)}){colours["clear"]}')
            sys.exit(1)

        size += os.path.getsize(os.path.join(root, file))
        percentage_filled = size / (1440 * 1024)
        # Render a progress bar
        filled_blocks = min(int(percentage_filled * columns), columns)
        colour = "yellow" if filled_blocks > 0.8 * columns else "green"
        colour = colour if filled_blocks < 0.99 * columns else "red"
        print(f"\r{colours[colour]}{block_drawing[0] * filled_blocks}{block_drawing[1] * (columns - filled_blocks)}{colours['clear']}", end="\r")
        if size > 1440 * 1024:
            break
        time.sleep(0.1)
    else:
        continue
    break

if size > 1440 * 1024:
    print(f'\n{colours["red"]}Warning: The folder size is larger than 1.44MB.\033[0m')
    sys.exit(1)
percentage = size / (1440 * 1024) * 100
size_kb = size // 1024
print(f'\n{colours[colour]}Disk is {percentage:.2f}% full ({size_kb}kb)\033[0m')

# Check the root of the folder for any files called "autorun.*"
# Pick it, and check if it's an executable file
# If it is, check how to run it using #! and run it

print("Loading program...")
for file in os.listdir(folder):
    if not file.lower().startswith("autorun."):
        continue
    if os.path.isfile(os.path.join(folder, file)):
        with open(os.path.join(folder, file), "rb") as f:
            if f.read(2) != b"#!":
                # Skip this file
                continue
            print("Executable found...")
            f.seek(2)
            # Get the interpreter
            interpreter = ""
            while (char := f.read(1)) != b"\n":
                interpreter += char.decode("utf-8")
            # Now execute the file
            time.sleep(1)
            print(f"Running '{interpreter}'...")
            time.sleep(0.5)
            print("-" * (columns - 1))
            os.system(f"{interpreter} '{os.path.join(folder, file)}'")
            sys.exit(0)
print(f"{colours['yellow']}No autorun script found.\033[0m")
